import React, { Component } from "react";

class Counter extends Component {
  state = {
    count: 0,
    imageUrl: "https://picsum.photos/200",
  };

  styles = {
    fontSize: 10,
    fontWeight: "bold",
  };

  render() {
    return (
      <React.Fragment>
        <span style="{this.styles}" className="badge badge-primary m-2">
          {this.formatCount()}
        </span>
        <button className="btn btn-secondary btn-sm">Increment</button>
      </React.Fragment>
    ); //jsx expression must have one parent element in this case div
  }

  formatCount() {
    const { count } = this.state;
    return count === 0 ? "Zero" : count; // count equals to zero then x applies otherwise count applies.
  }
}

export default Counter;
