import React, { Component } from "react";

class Counter extends Component {
  state = {
    value: this.props.value,
  };

  handleIncrement = () => {
    this.setState({ value: this.state.value + 1 });
  };

  render() {
    return (
      <div>
        <span className={this.getBadgeClasses()}>{this.formatCount()}</span>
        <button
          onClick={(product) => this.handleIncrement(product)}
          className="btn btn-secondary btn-"
        >
          +
        </button>
        <button className="btn btn-danger btn-sm m-2">Delete</button>
      </div> //jsx expression must have one parent element in this case div
    );
  }

  getBadgeClasses() {
    let classes = "badge m-2 badge-"; // what always there
    classes += this.state.value === 0 ? "warning" : "primary"; // if count 0 then badge-warning applies otherwise badge-primary applies.
    return classes;
  }

  formatCount() {
    const { value } = this.state;
    return value === 0 ? "Zero" : value; // count equals to zero then x applies otherwise count applies.
  }
}

export default Counter;
