import React, { Component } from "react";

class Counter extends Component {
  state = {
    count: 5,
    imageUrl: "https://picsum.photos/200",
  };

  render() {
    return (
      <React.Fragment>
        <span className={this.getBadgeClasses()}>{this.formatCount()}</span>
        <button className="btn btn-secondary btn-sm">Increment</button>
      </React.Fragment>
    ); //jsx expression must have one parent element in this case div
  }

  getBadgeClasses() {
    let classes = "badge m-2 badge-"; // what always there
    classes += this.state.count === 0 ? "warning" : "primary"; // if count 0 then badge-warning applies otherwise badge-primary applies.
    return classes;
  }

  formatCount() {
    const { count } = this.state;
    return count === 0 ? "Zero" : count; // count equals to zero then x applies otherwise count applies.
  }
}

export default Counter;
