import React from "react";

const Stats = (props) => {
  const totalTasks = props.tasks.length;

  return (
    <table className="stats">
      <tbody>
        <tr>
          <td>Total Tasks:</td>
          <td> {totalTasks} </td>
        </tr>
      </tbody>
    </table>
  );
};

export default Stats;
