import React, { Component } from "react";

import Header from "./Header"; // path as a string, .js extension not req.
import Footer from "./Footer";
import Player from "./Player"; // path as a string, .js extension not req.
import AddPlayerForm from "./AddNewTask";

class App extends Component {
  state = {
    players: [
      {
        name: "Guil",
        score: 0,
        id: 1,
      },
      {
        name: "Treasure",
        score: 0,
        id: 2,
      },
      {
        name: "Ashley",
        score: 0,
        id: 3,
      },
      {
        name: "James",
        score: 0,
        id: 4,
      },
    ],
  };

  // player id counter
  prevPlayerId = 4;

  handleScoreChange = (index, delta) => {
    this.setState((prevState) => ({
      score: (prevState.players[index].score += delta),
    }));
  };

  handleAddPlayer = (name) => {
    this.setState({
      players: [
        {
          name: name,
          score: 0,
          id: (this.prevPlayerId += 1),
        },
        ...this.state.players, // spread operator to clone existing players
      ],
    });
  };

  handleRemovePlayer = (id) => {
    this.setState((prevState) => {
      return {
        players: prevState.players.filter((p) => p.id !== id),
      };
    });
  };

  render() {
    return (
      <div className="scoreboard">
        <Header title="Scoreboard" players={this.state.players} />

        {/* Players list */}
        {this.state.players.map((player, index) => (
          <Player
            name={player.name}
            score={player.score}
            id={player.id}
            key={player.id.toString()}
            index={index}
            changeScore={this.handleScoreChange}
            removePlayer={this.handleRemovePlayer}
          />
        ))}
        <AddNewTask addPlayer={this.handleAddPlayer} />
        <Footer />
      </div>
    );
  }
}

export default App;
