import React from "react";

const Stats = (props) => {
  const totalPlayers = props.tasks.length;

  // Reduce method
  const totalPoints = props.tasks.reduce((total, player) => {
    return total + player.score;
  }, 0);

  return (
    <table className="stats">
      <tbody>
        <tr>
          <td>Tasks:</td>
          <td> {totalPlayers} </td>
        </tr>
        <tr>
          <td>Total Points:</td>
          <td> {totalPoints} </td>
        </tr>
      </tbody>
    </table>
  );
};

export default Stats;
